namespace Dhl\Shipping\Webservice\ResponseType\Generic;

/**
 * ResponseStatus
 *
 * @package  Dhl\Shipping
 * @author   Christoph Aßmann <christoph.assmann@netresearch.de>
 * @license  http://opensource.org/licenses/osl-3.0.php Open Software License (OSL 3.0)
 * @link     http://www.netresearch.de/
 */
class ResponseStatus implements ResponseStatusInterface
{
    /**
     * @var int
     */
    private $code;

    /**
     * @var string
     */
    private $text;

    /**
     * @var string
     */
    private $message;

    /**
     * ResponseStatus constructor.
     *
     * @param int    $code
     * @param string $text
     * @param string $message
     */
    public function __construct($code, $text, $message)
    {
        $this->code = $code;
        $this->text = $text;
        $this->message = $message;
    }

    /**
     * @return int
     */
    public function getCode()
    {
        return $this->code;
    }

    /**
     * @return string
     */
    public function getText()
    {
        return $this->text;
    }

    /**
     * @return string
     */
    public function getMessage()
    {
        return $this->message;
    }

    /**
     * @return bool
     */
    public function isSuccess()
    {
        return ($this->code !== self::STATUS_FAILURE);
    }

    /**
     * @return bool
     */
    public function isError()
    {
        return ! $this->isSuccess();
    }
}
